var closePromo=document.querySelector(".app-promo__close"),promoBlock=document.querySelector(".app-promo");function closePromoBlock(e){e.preventDefault(),promoBlock.classList.add("hidden")}closePromo.addEventListener("click",closePromoBlock);var overlay=document.querySelector(".overlay"),shareWindow=document.querySelector(".share-popup"),btnClosePopup=document.querySelector(".share-popup__close"),shareBtn=document.querySelector(".sharing-buttons__link--share"),shareIcon=document.querySelectorAll(".sharing__link--share");btnClosePopup.addEventListener("click",closePopup),shareBtn.addEventListener("click",openPopup);for(var i=0;i<shareIcon.length;i++)shareIcon[i].addEventListener("click",openPopup);function openPopup(e){e.preventDefault(),overlay.classList.remove("hidden"),shareWindow.classList.remove("hidden")}function closePopup(e){e.preventDefault(),overlay.classList.add("hidden"),shareWindow.classList.add("hidden")}window.addEventListener("keydown",function(e){27===e.keyCode&&(overlay.classList.add("hidden"),shareWindow.classList.add("hidden"))});